import React, { Component } from 'react';
import './App.css';

import Task from './Components/Task';

class App extends Component {

  state = {
    list : [
      {
        id: 1,
        todo: 'Buy Milk'
      }, 
      {
        id: 2,
        todo: 'Pick up mail'
      },
      {
        id: 3,
        todo: 'Walk the dog'
      }
    ]
  }


  render() {
    return (
      <div className="App">

          {
            this.state.list.map((task) => {
              return (
                <div>
                  {task.todo}
                </div>
              )
            })
          }

      </div>
    )
  }


}

export default App;
