import React, { Component } from 'react';

class Task extends Component {

    state = {
        taskInput: ''
    }

    showTaskList = () => {
        return this.props.list.map((task) => {
            return (
              <div key={task.id}>
                {task.todo}
              </div>
            )
          })

    }

    taskHandleInput = (event) => {
        //console.log(`name: ${event.target.name} || value ${event.target.value}`)
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    taskHandleSubmit = (event) => {
        event.preventDefault();

        this.props.appHandleSubmit(this.state.taskInput);
    }


    render() {
        return (
            <div>
                <form onClick={this.taskHandleSubmit} ref={element => this.form = element}>
                    <input 
                        onChange={this.taskHandleInput} 
                        name="taskInput" 
                    />
                    <button>Submit</button>
                </form>

                {this.showTaskList()} 
            </div>
        )
    }

}

export default Task;
