                                value={task.todo} 
                                onChange={this.taskListHandleEdit.bind(this)} 
                                name="newInput"
                                /> 
                                : todo } 
                <button onClick={() => this.taskListHandleToggle(task.id)} style={{marginLeft: '5px', marginRight: '5px'}}>Edit</button>
                <button onClick={() => this.taskListHandleDeleteByID(task.id)}>Delete</button>
            </div>
            )
          })
    }

    taskHandleDeleteByID = (taskID) => {
        this.props.appHandleDeleteByID(taskID);
    }

    taskHandleInput = (event) => {
        //console.log(`name: ${event.target.name} || value ${event.target.value}`)
        //<button onClick={() => this.taskHandleDeleteByID(task.id)}>Delete</button>
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    taskHandleSubmit = (event) => {
        event.preventDefault();

        this.props.appHandleSubmit(this.state.taskInput);
        this.form.reset();
    }

    render() {
        return (
            <div>
                <form onSubmit={this.taskHandleSubmit} ref={element => this.form = element} style={{marginBottom: '5px'}}>
                    <input 
                        onChange={this.taskHandleInput} 
                        name="taskInput" 
                    />
                    <button>Submit</button>
                </form>

                {this.showTaskList()} 
            </div>
        )
    }

}

export default Task;
