import React, { Component } from 'react';
import './App.css';

import Task from './Components/Task';

class App extends Component {

  state = {
    list : [
      {
        id: 1,
        todo: 'Buy Milk'
      }, 
      {
        id: 2,
        todo: 'Pick up mail'
      },
      {
        id: 3,
        todo: 'Walk the dog'
      }
    ]
  }

  render() {

    let showTask = (
      this.state.list.map((task, index) => {
        return (
          <div key={index}>
            {task.todo}
          </div>
        )
      })
    )

    return (
      <div className="App">

          {showTask}

      </div>
    )
  }


}

export default App;
