import React, { Component } from 'react';
import './App.css';

import Task from './Components/Task';

class App extends Component {

  state = {
    list : [
      {
        id: 1,
        todo: 'Buy Milk'
      }, 
      {
        id: 2,
        todo: 'Pick up mail'
      },
      {
        id: 3,
        todo: 'Walk the dog'
      }
    ]
  }

  showTaskFunc = () => {
    return this.state.list.map((task, index) => {
      return (
        <div key={index}>
          {task.todo}
        </div>
      )
    })
  } 

  handleInput = (event) => {
    console.log(event.target.name)
  }

  render() {

    return (
      <div className="App">

          <div>
            <input onChange={this.handleInput} name="taskInput" />
          </div>

          {this.showTaskFunc()}

      </div>
    )
  }


}

export default App;
