import React, { Component } from 'react'

class TaskList extends Component {

  state = {
      isToggle: false
  }  

  taskListHandleDeleteByID = (id) => {
      this.props.taskHandleDeleteByIDProps(id)
  }

  taskListHandleToggle = () => {
    this.setState({
        isToggle: !this.state.isToggle
    })
  }

  taskListHandleEdit = () => {

  }

  componentWillMount() {
      console.log('Component Will Mount');
  }

  componentDidMount() {
      console.log('Component Did Moutn')
  }

  render() {

    console.log('render')

    const { todo, id } = this.props.task;
    const { isToggle } = this.state; 
    return (
        <div style={{marginBottom: '10px'}}>
            {  isToggle ? <input 
                            value={todo} 
                            onChange={this.taskListHandleEdit} 
                            name="newInput"
                            /> 
                            : todo } 
            <button onClick={() => this.taskListHandleToggle()} style={{marginLeft: '5px', marginRight: '5px'}}>Edit</button>
            <button onClick={() => this.taskListHandleDeleteByID(id)}>Delete</button>
        </div>
    )
  }
}

export default TaskList;
